	@Test
	public void testEncode() {
		//  GBK to Unicode
		String key = "清华大学";
		String returnString = CommonUtil.encode(key);
		System.out.println(returnString);
		Assert.assertEquals("%E6%B8%85%E5%8D%8E%E5%A4%A7%E5%AD%A6", returnString);
	}

	@Test
	public void testUnicode2GBK() {
		// 测试 搜索baidu返回值中中文转码  Unicode to GBK
		String returnString = CommonUtil.Unicode2GBK("\u6559\u80b2,\u5b66\u6821,\u5927\u5b66");
		System.out.println(returnString);
		Assert.assertEquals("教育,学校,大学", returnString);
	}

